mapper.update:
update METTS_UP.USERS set
  MONEY =   #{MONEY},
  FOOD =  #{FOOD},
  SCORE =   #{SCORE}
where UUID = #{UUID}

update METTS_UP.USERS set
  MONEY = MONEY + #{ACQUIREMONEY},
  FOOD = FOOD + #{ACQUIREFOOD},
  SCORE = SCORE + #{ACQUIRESCORE}
where UUID = #{MYUUID}

update METTS_UP.USERS set
  MONEY = MONEY + #{LOSTMONEY},
  FOOD = FOOD + #{LOSTFOOD},
  SCORE = SCORE + #{LOSTSCORE}
where UUID = #{ENEMYUUID}

mapper.insert:
insert into USERS (UUID,PASSWORD, USERNAME, MONEY, FOOD, SCORE) values (
	#{UUID},#{PASSWORD}, #{USERNAME}, #{MONEY}, #{FOOD}, #{SCORE}
)
